import {React, useState, useEffect} from 'react';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';
import TimeField from 'react-simple-timefield';

import { CKEditor } from '@ckeditor/ckeditor5-react';
import Editor from 'ckeditor5-custom-build/build/ckeditor';

import "./MCPage.css"

export default function MCPage(){
    const history = useHistory();

    const [role, GetUserRole] = useState('')
    const [create, GetCreate] = useState(false)
    const [confessid, GetConfessId] = useState('')
    const [publishstatus, GetPublishStatus] = useState('')
    const [timer, GetTimer] = useState('')

    const editorConfiguration = {
        placeholder: "Type your confession here...",
        toolbar: ["bold", "italic", "underline"]
    }

    const createconfession = () => {
        GetCreate(true)
        Axios.post("http://localhost:3001/createconfession", {
        }).then((response) => {
            if (response.data) {
                GetConfessId(response.data[0].confess_id)
            }
        })
    }
    
    function updatetitle(e) {
        console.log(confessid)
        Axios.post("http://localhost:3001/updateconfession/title", {
            confess_id: confessid,
            title: e.target.value  
        })
    }

    const updatecontent = (event, editor) => {
        Axios.post("http://localhost:3001/updateconfession/content", {
            confess_id: confessid,
            content: editor.getData()
        })
    }

    const publishConfession = () => {
        console.log(confessid)
        Axios.post("http://localhost:3001/updateconfession/publish", {
            confess_id: confessid,
            draft: "0"
        }).then((response) => {
            if (response.data.message) {
                GetPublishStatus(response.data.message)
            }
        })
    }

    const updatetime = (event, value) => {
        console.log(value)
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                GetUserRole(result.data.user[0].role)
            } 
            else {
                history.push("/")
            }
        });

    }); // remove the , [] later on 

    return (
        <div className="MCPage">
            <h2>Make Confession Page</h2>
            {create === false && <button onClick={createconfession}>Create confession</button>}
            {create === true && 
            <div className="Box">
                <div>
                    <input placeholder="Confession Title..."
                    onChange={updatetitle}/>
                </div>
                <div>
                    <CKEditor
                        editor = { Editor }
                        data = ""
                        config = {editorConfiguration}
                        onChange = {updatecontent}                       
                    />
                </div>
                <section>
                    <TimeField
                        value = "00:00:00"
                        onChange={updatetime}
                        style={{
                            border: '2px solid #666',
                            fontSize: 42,
                            width: 167,
                            padding: '5px 8px',
                            color: '#333',
                            borderRadius: 3
                          }}
                    />
                </section>
                <button onClick={publishConfession}>Publish</button>
                <p>{publishstatus}</p>
            </div>}
        </div>
    )
}
