import {React, useState, useEffect} from 'react';
import "./LandingPage.css";
import Modal from "react-responsive-modal";
import {
    BrowserRouter as Router,
    Switch,
    Route,
  } from "react-router-dom";

import { useHistory } from 'react-router-dom';
import Axios from 'axios';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import HomePage from "../HomePage/HomePage";

import Validation from './Validation';

export default function LandingPage(){
    const history = useHistory();

    const [SSstatus, setSSstatus] = useState(0); // SS = sign in sign up
    
    // set (Sign in) Status
    const signInS = () => {
        setSSstatus(1)
    }

    // set Sign Up status
    const signUpS = () => {
        setSSstatus(2)
    }
    
    const loginfunc = (result) => {
        Axios.post("http://localhost:3001/signin", {
            username: result.userName,
            password: result.password
        }).then((response) => {
            if (response.data.message) {
                console.log(response.data.message)
            } else {
                history.push("/home")
            }
        })
    }

    const signupfunc = (result) => {
        Axios.post("http://localhost:3001/signup", {
            firstname: result.firstName,
            lastname: result.lastName,
            username: result.userName,
            email: result.email,
            dob : result.dob,
            password: result.password,
        }).then((response) => {
            if (response.data.message) {
                console.log(response.data.message);
            }
        })
    }

    return(
        <div className="landingPage">
            <div className="LandingDivRight">
                <button id="signIn" onClick={signInS}>Sign In</button>
                <button id="signUp" onClick={signUpS}>Sign Up</button>
            </div>
            {SSstatus === 1 && <div>
            {/* LOGIN PAGE */}
                <Modal open={true}>
                <div className="LoginPage">
                    <h2>Login Page</h2>
                    <Formik
                    initialValues={{
                    userName: "",
                    password: "",
                    }}
                    validationSchema={Validation}
                    onSubmit={loginfunc}
                    >
                    <Form style={{ display: "flex", flexDirection: "column" }}>

                    <Field name="userName" type="text" placeholder="Username or Email" />
                    <ErrorMessage
                        name="userName"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field name="password" type="password" placeholder="Password" />
                    <ErrorMessage
                        name="password"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <div className="row">
                        <p className="signintext">Don't have an account? Sign Up <a href="/signup">here</a>.</p>
                        <div className="flex"></div>
                        <button className="btn" type="submit">Submit</button>
                    </div>
                    </Form>
                </Formik>
                </div>
                </Modal>
            </div>}
            {/* SIGN UP PAGE */}
            {SSstatus === 2 && <div>
                <Modal>
                    <div className="SignUpPage">
                    <h2>Sign up Page</h2>
                    <Formik
                        initialValues={{
                        firstName: "",
                        lastName: "",
                        userName: "",
                        email: "",
                        dob: "",
                        password: "",
                        }}
                        validationSchema={Validation}
                        onSubmit={signupfunc}
                    >
                    <Form style={{ display: "flex", flexDirection: "column" }}>
                    <Field className="form-control" name="firstName" type="text" placeholder="First Name" />
                    <ErrorMessage
                        name="firstName"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="form-control" name="lastName" type="text" placeholder="Last Name" />
                    <ErrorMessage
                        name="lastName"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="form-control" name="userName" type="text" placeholder="User Name" />
                    <ErrorMessage
                        name="userName"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="form-control" name="email" type="email" placeholder="Email" />
                    <ErrorMessage
                        name="email"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="form-control" name="dob" type="date" placeholder="Date of Birth" />
                    <ErrorMessage
                        name="dob"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="password" name="password" type="password" placeholder="Password" />
                    <ErrorMessage
                        name="password"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <div className="row">
                        <p className="signintext">Have an account? Login <a onClick={this.onOpenModalLogin}>here</a>.</p>
                        <div className="flex"></div>
                        <button className="btn" type="submit">Submit</button>
                    </div>
                    </Form>
                </Formik>
                </div>
                </Modal>
            </div>}
        </div>
    )
}
