import React, {useState} from 'react';
import Axios from 'axios';
import {Link} from "react-router-dom";
import logo from '../../images/logo.png';

import "./NavBar.css";

import TagOption from '../../GlobalVar/TagOption';
import TimerOption from '../../GlobalVar/TimerOption';

// create confession
import { CKEditor } from '@ckeditor/ckeditor5-react';
import Editor from 'ckeditor5-custom-build/build/ckeditor';
import Select from 'react-select';
import Modal from "react-responsive-modal";

export default function NavBar() {  
    const [ModalStatus, SetModalStatus] = useState(false)

    const [title, GetTitle] = useState('')
    const [content, GetContent] = useState('')
    const [location, GetLocation] = useState('')
    const [timer, GetTimer] = useState('0')
    const [Tag, GetTag] = useState([])

    const editorConfiguration = {
        placeholder: "Type your confession here...",
        toolbar: ["bold", "italic", "underline"]
    }

    const logout = () => {
        Axios.post("http://localhost:3001/logout", {
        })
    }

    const openModal = () => {
        SetModalStatus(true)
    }

    const closeModal = () => {
        SetModalStatus(false)
    }

    const postConfession = (draft) => {
        
    }

    return ( 
        <div>
            <nav className="navBar">
                <li>
                    <a href="/home" className="options"><Link to="/home"><img className="logoClass" src={logo}/></Link></a> {/* i delete src={logo}*/}
                </li>
                <li>
                    <a href="/draft" className="options"><Link to="/draft">Confession Draft</Link></a>
                </li>
                <li>
                    <a className="options" onClick={openModal}>Make Confession</a>
                </li>
                <li> {/* if can this at the bottom of the page ya */}
                    <a href="/" className="options"><Link to="/" onClick={logout}>Logout</Link></a>
                </li>
            </nav>
            {ModalStatus === true && <div>
                <Modal open={true} onClose={closeModal}>
                    <div className="modal-createconfession">
                        <div>
                            <label> Title:
                                <input 
                                maxLength = "250"
                                placeholder="Spice your confession!"
                                onChange={(e) => {
                                    GetTitle(e.target.value);
                                }}
                                />
                            </label>
                        </div>
                        <div>
                            <CKEditor
                                editor = { Editor }
                                data = {content}
                                config = {editorConfiguration}
                                onChange = {(event, editor) => {
                                    const data = editor.getData()
                                    GetContent(data);
                                }}                       
                            />
                        </div>
                        <div>
                            <label> Crime scene:
                                <input 
                                maxLength = "150"
                                placeholder="Where did it take place?"
                                onChange={(event) => {
                                    GetLocation(event.target.value);
                                }}
                                />
                            </label>
                        </div>
                        <Select
                            defaultValue = {timer}
                            onChange = {GetTimer}
                            options = {TimerOption}/>
                        <Select
                            defaultValue = {Tag}
                            onChange = {GetTag}
                            isMulti
                            options = {TagOption}/>
                        <button onClick={() => {postConfession(1)}}>Save to Draft</button>
                        <button onClick={() => {postConfession(0)}}>Publish Confession</button>
                    </div>
                </Modal>
            </div>}
        </div>
    )
}
