import React, {useEffect, useState} from 'react';
import NavBar from '../../components/NavBar/NavBar';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';

import "./AdminPage.css"

export default function AdminPage() {
    const history = useHistory();

    const [role, GetUserRole] = useState('')

    const seetable = (tname) => {
        Axios.post("http://localhost:3001/admin/seetable", {
            table: tname
        })
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.user !== undefined) {
                if (result.data.user[0].usertype === "admin") {
                    GetUserRole(result.data.user[0].usertype)
                }
                else {
                    history.push("/")
                }
            } else {
                history.push("/")
            }
        });
    }, []); // remove the , [] later on 
    
    return (
        <div className="admin-page">
            {role === "admin" && <div>
                {/*<NavBar/>*/}
                <div>
                    <h1>All User</h1>
                </div>
                <div className="row">
                    <button className="all-table-btn" onClick={() => {seetable(comments)}}>Comment Table</button>
                    <button className="all-table-btn" onClick={() => {seetable(confession)}}>Confession Table</button>
                    <button className="all-table-btn" onClick={() => {seetable(likes)}}>Likes Table</button>
                    <button className="all-table-btn" onClick={() => {seetable(tags)}}>Tags Table</button>
                    <button className="all-table-btn" onClick={() => {seetable(user)}}>User Table</button>
                </div>
            </div>}
        </div>
    )
}
