import React, {useEffect, useState} from 'react';
import NavBar from '../../components/NavBar/NavBar';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';
import Table from 'react-bootstrap/Table'

import "./AdminPage.css"

export default function AdminPage() {
    const history = useHistory();

    const [role, GetUserRole] = useState('')
    const [userdata, GetUserData] = useState([])
    const [OTStatus, SetOTStatus] = useState(0) // Open Table

    const usertable = () => {
        Axios.post("http://localhost:3001/admin/seetable", {
            table: "user"
        }).then((result) => {
            GetUserData(result.data)
            SetOTStatus(1)
        })
    }
    const tagtable = () => {
        Axios.post("http://localhost:3001/admin/seetable", {
            table: "tags"
        }).then((result) => {
            GetUserData(result.data)
        })
    }
    const likestable = () => {
        Axios.post("http://localhost:3001/admin/seetable", {
            table: "likes"
        }).then((result) => {
            GetUserData(result.data)
        })
    }
    const confessiontable = () => {
        Axios.post("http://localhost:3001/admin/seetable", {
            table: "confession"
        }).then((result) => {
            GetUserData(result.data)
        })
    }
    const commenttable = () => {
        Axios.post("http://localhost:3001/admin/seetable", {
            table: "comments"
        }).then((result) => {
            GetUserData(result.data)
        })
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.user !== undefined) {
                if (result.data.user[0].usertype === "admin") {
                    GetUserRole(result.data.user[0].usertype)
                }
                else {
                    history.push("/")
                }
            } else {
                history.push("/")
            }
        });
    }, []); // remove the , [] later on 
    
    return (
        <div className="admin-page">
            {role === "admin" && <div>
                {/*<NavBar/>*/}
                <div>
                    <h1>All User</h1>
                </div>
                <div className="row">
                    <button className="all-table-btn" onClick={commenttable}>Comment Table</button>
                    <button className="all-table-btn" onClick={confessiontable}>Confession Table</button>
                    <button className="all-table-btn" onClick={likestable}>Likes Table</button>
                    <button className="all-table-btn" onClick={tagtable}>Tags Table</button>
                    <button className="all-table-btn" onClick={usertable}>User Table</button>
                </div>
                {OTStatus === 1 && <div className="user-table-display">
                    <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>Email</th>
                            <th>AnonTag</th>
                            <th>DOB</th>
                            <th>UserType</th>
                            <th>Join Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {userdata.map((val) => {
                            return (
                                <>
                                <tr>
                                    <th>{val.id}</th>
                                    <th>{val.email}</th>
                                    <th>{val.anontag}</th>
                                    <th>{val.DOB}</th>
                                    <th>{val.usertype}</th>
                                    <th>{val.joindate}</th>
                                </tr>
                                </>
                            )
                        })}
                    </tbody>
                    </Table>
                </div>}
            </div>}
        </div>
    )
}
