import {React, useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import Axios from 'axios';

import { CKEditor } from '@ckeditor/ckeditor5-react';
import Editor from 'ckeditor5-custom-build/build/ckeditor';

import Select from 'react-select';

import useUnsavedChangesWarning from "../../hooks/useUnsavedChangesWarning";

import "./EditablePage.css"

export default function EditablePage(){
    const history = useHistory();

    const [Prompt, setDirty] = useUnsavedChangesWarning();
    const [openDraft, GotDraft] = useState(false);
    const [publishstatus, GetPublishStatus] = useState('');
    const [draftList, GetDraftList] = useState([]);

    const [CId, SetCId] = useState(0);
    const [location, SetLocation] = useState('');
    const [title, SetTitle] = useState('');
    const [content, SetContent] = useState('');
    const [timer, SetTimer] = useState('0');
    const [tag, SetTag] = useState([]);

    const editorConfiguration = {
        placeholder: "Type your confession here...",
        toolbar: ["bold", "italic", "underline"]
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                Axios.get("http://localhost:3001/confession/getdraft").then((response) => {
                    if (response.data) {
                        GetDraftList(response.data)
                    }
                })
            } else {
                history.push("/")
            }
        });

    }, []); // remove the , [] later on 

    const savetoDraft = () => {
        Axios.post("http://localhost:3001/updateconfession/draft", {
            location: location,
            content: content,
            title: title,
        }).then((response) => {
            if (response.data.message) {
                GetPublishStatus(response.data.message)
            }
        })
    }

    const publishConfession = () => {
        let usertimer = 0
        var tagarray = []
        if (timer.value !== undefined) {
            usertimer = timer.value
        }

        for (var i = 0; i < tag.length; i++) {
            delete tag[i].label
            tagarray.push(tag[i].value)
        } 

        Axios.post("http://localhost:3001/updateconfession/publish", {
            location: location,
            content: content,
            title: title,
            timer: usertimer,
            tag: tagarray,
        }).then((response) => {
            if (response.data.message) {
                GetPublishStatus(response.data.message)
            }
        })
    }

    const FindDraftID = (conID) => {
        const currentID = `${conID}`
        GotDraft(true)

        Axios.post("http://localhost:3001/confession/getdraft/specific", {
            confessID: currentID
        }).then((response) => {
            if (response.data) {
                console.log(response.data[0].confess_id)
                var array = Object.values(response.data[0])
                console.log(array[0])
                SetCId(response.data[0].confess_id)
                console.log(CId)
            }
        })
    }

    const TimerOption = [
        {value: '12', label: 'Half Day'},
        {value: '24', label: 'One Day'},
        {value: '48', label: 'Two Days'},
        {value: '72', label: 'Three Days'},
        {value: '0', label: 'Permanent'},
    ]

    const TagOption = [
        {value: 'relationship', label: 'Relationship'},
        {value: 'thief', label: 'Thief'},
        {value: 'sabotage', label: 'Sabotage'},
        {value: 'karma', label: 'Karma'},
        {value: 'bday', label: 'Birthday'},
        {value: 'eventruin', label: 'Event Ruiner'},
    ]

    return (
        <div className="Draft Page">
            <h2>Your draft</h2>
            {openDraft === false && <div>
                {draftList.map((val) => {
                    return (
                        <div className="list-draft-box">
                            <div className="clickable-box" onClick={() => {FindDraftID(val.confess_id)}}>
                                {val.confess_id}
                            </div>
                        </div>
                    )
                })}
            </div>}
            {openDraft === true && <div>
                <div>
                    <label> Title:
                        <input 
                        value = {title}
                        maxLength = "250"
                        placeholder="Spice your confession!"
                        onChange={(e) => {
                            SetTitle(e.target.value);
                            setDirty();
                        }}
                        />
                    </label>
                </div>
                <div>
                    <CKEditor
                        editor = { Editor }
                        data = {content}
                        config = {editorConfiguration}
                        onChange = {(event, editor) => {
                            const data = editor.getData()
                            SetContent(data);
                            setDirty();
                        }}                       
                    />
                </div>
                <div>
                    <label> Crime scene:
                        <input 
                        value = {location}
                        maxLength = "150"
                        placeholder="Where did it take place?"
                        onChange={(event) => {
                            SetLocation(event.target.value);
                            setDirty();
                        }}
                        />
                    </label>
                </div>
                <Select
                    defaultValue = {timer}
                    onChange = {SetTimer}
                    options = {TimerOption}/>
                <Select
                    defaultValue = {tag}
                    onChange = {SetTag}
                    isMulti
                    options = {TagOption}/>
                <button onClick={() => {
                    savetoDraft();
                }}>Save to Draft</button>
                <button onClick={publishConfession}>Publish Confession</button>
                <p>{publishstatus}</p>
            </div>}
        </div>
    )
}
