import React, {useEffect, useState} from 'react';
import NavBar from '../../components/NavBar/NavBar';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';

import Modal from "react-responsive-modal";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import Editor from 'ckeditor5-custom-build/build/ckeditor';
import parse from 'html-react-parser';

import "./YourPostPage.css"

export default function YourPostPage() {
    const history = useHistory();

    const [confessionList, GetConfessionList] = useState([])
    const [CommentMode, SetCommentMode] = useState(false)
    const [CommentList, GetCommentList] = useState([])
    const [comment, GetComment] = useState("")
    const [LikedList, GetLikedList] = useState([])
    const [usertag, GetUserTag] = useState("")
    
    const editorConfiguration = {
        placeholder: "Type your comment here...",
        toolbar: ["bold", "italic", "underline"]
    }

    const likeConfession = (conID) => {
        Axios.post("http://localhost:3001/confession/like", {
            confessid: conID
        })
        window.location.reload(); // remove this if want to use useeffect that remove , [] (testing)
    }

    const getComment = (con_ID) => {
        SetCommentMode(true)
        console.log(con_ID)
        Axios.post("http://localhost:3001/confession/comment/get", {
            confessid: con_ID
        }).then((response) => {
            GetCommentList(response.data)
        })
    }

    const postcomment = () => {
        Axios.post("http://localhost:3001/confession/comment/post", {
            content: comment
        }).then((response) => {
            if (response.data.message) {
                GetComment("")
                SetCommentMode(false)
            }
        })
    }

    const closemodal = () => {
        SetCommentMode(false)
    }

    const clicktag = (tag) => {
        Axios.post("http://localhost:3001/tag/store", {
            clicktag: tag
        })
        history.push(`/search/${tag}`)
    }

    const deleteconfession = (conID) => {
        Axios.delete(`http://localhost:3001/confession/delete/${conID}`)
        window.location.reload();
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                GetUserTag(result.data.user[0].anontag)
                Axios.get("http://localhost:3001/confession/userpost/getall").then((response) => {
                    if (response.data) {
                        GetConfessionList(response.data)
                    }
                })
                Axios.get("http://localhost:3001/confession/likes/compare").then((result) => {
                    if (result.data) {
                        GetLikedList(result.data[0].liked_confession)
                    }
                })
            }
            else {
                history.push("/")
            }
        });
    }, []); // remove the , [] later on 
    
    return (
        <div className="YourPostPage">
            <div className="navBarDiv">
                <NavBar/>
            </div>

            <div className="homeHeader">
                <h1>Your Post</h1>
            </div>
            
            <div className="homepageContainer">
                {confessionList.map((val) => {
                    return (
                        <>
                        <div className="displayCard">
                            <div className="confessOwner">
                                <p>Confession by: <u>{val.confess_owner}</u></p>
                            </div>

                            <div className="confessTitle">
                                <label>Confess Title: </label>
                                {val.confess_title}
                            </div>
                            
                            <div className="confessContent">
                                <label>Confession:</label>
                                <p>{parse(val.confess_content)}</p>
                            </div>

                            <div className="confessLocation">
                                <label>Location: </label>
                                {val.confess_location}
                            </div>

                            <div className="confessTime">
                                Posted at: {val.date_published.substring(0,10)}
                            </div>

                            <div className="like-area">
                                <label>Number of likes: {val.likes}</label>
                                {LikedList.includes(val.confess_id) === false && <button onClick={() => likeConfession(val.confess_id)}>Like</button>}
                                {LikedList.includes(val.confess_id) === true && <button onClick={() => likeConfession(val.confess_id)}>Unlike</button>}
                            </div>

                            <div className="comment-area">
                                <label>Number of comments: {val.comnum}</label>
                                <button onClick={() => getComment(val.confess_id)}>Comment</button>
                            </div>

                            <div className="block1"></div>

                            <div className="confessTags">
                                <label>Tags: </label>
                                {val.confess_tag.map((value) => {
                                    return(
                                        <button className="tag-btn" onClick={() => clicktag(value)}>{value}</button>
                                    )
                                })}
                            </div>
                            {(usertag === val.confess_owner) === true && <button onClick={() => deleteconfession(val.confess_id)}>Delete</button>}

                            {CommentMode === true && <div>
                                <Modal open={true} onClose={closemodal}>
                                    <CKEditor
                                        editor = { Editor }
                                        data = ""
                                        config = {editorConfiguration}
                                        onChange = {(event, editor) => {
                                            const data = editor.getData()
                                            GetComment(data);
                                        }}                       
                                    />
                                    <button onClick={postcomment}>Enter</button> 
                                    <div className="all-comments">
                                        <h3>Comments:</h3>
                                        {CommentList.map((value) => {
                                            return (
                                                <>
                                                    <label>{value.comment_owner} wrote: </label>
                                                    <p>{parse(value.comment_content)}</p>
                                                </>
                                            )
                                        })}
                                    </div>
                                </Modal>
                            </div>}
                        </div>
                        </>
                    )
                })}
            </div>
        </div>
    )
}
