import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';

import "./RSNav.css";

export default function RSNav() {  
    const history = useHistory();

    const [taglist, GetTagList] = useState([])

    const TagLink = (tag) => {
        Axios.post("http://localhost:3001/tag/store", {
            clicktag: tag
        })
        history.push(`/search/${tag}`)
    }

    Axios.get("http://localhost:3001/tag/get").then((response) => {
        GetTagList(response.data)
    })
    return ( 
        <div className="right-side-nav">
            <div className="trendingTags">
                <h3>Trending Tags</h3>
            
                <div className="rs-tag-list">
                    {taglist.map((val) => {
                        return (
                        <>
                        {val.tagcount > 0 && <div className="row"> 
                                <label><button onClick={() => TagLink(val.tag)}>{val.tag}<br/>{val.tagcount} Confessions</button></label>
                            </div>
                        }
                        </>
                        )
                    })}
                </div>
            </div>
        </div>
    )
}
