import React, {useEffect, useState} from 'react';
import NavBar from '../../components/NavBar/NavBar';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';
import troll from '../../images/pentol.png';

import "./AdminTagPage.css"

export default function AdminTagPage() {
    const history = useHistory();

    const [role, GetUserRole] = useState('')
    const [tagname, GetTagName] = useState('')
    const [taglist, GetTagList] = useState([])

    const addTag = () => {
        Axios.post("http://localhost:3001/admin/tag/create", {
            tagname: tagname
        })
        window.location.reload();
    }

    const TagLink = (tag) => {
        Axios.post("http://localhost:3001/tag/store", {
            clicktag: tag
        })
        history.push(`/search/${tag}`)
    }

    const DeleteTag = (tagid) => {
        Axios.delete(`http://localhost:3001/admin/tag/delete/${tagid}`,)
        window.location.reload();
    }

    const backToHome = () => {
        history.push('/home');
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.user !== undefined) {
                if (result.data.user[0].usertype === "admin") {
                    GetUserRole(result.data.user[0].usertype)
                    Axios.get("http://localhost:3001/tag/get").then((response) => {
                        GetTagList(response.data)
                    })
                }
                else {
                    history.push("/")
                }
            } else {
                history.push("/")
            }
        });
    }, []); // remove the , [] later on 
    
    return (
        <div className="admin-tag-page">
            {role === "admin" && <div className="adminAlign">
                {/*<NavBar/>*/}
                <div className="adminRow">
                <button className="backButton" onClick={backToHome}>Home</button>
                    <img className="pentol" src={troll}></img>
                    <h1>ALL TAGS</h1>
                    <img className="pentol" src={troll}></img>
                </div>
                <div className="list-tags">
                <div className="add-tags">
                    <input placeholder="Add tags..." onChange={(e) => {GetTagName(e.target.value)}}></input>
                    <button onClick={addTag}>Add</button>
                </div>
                    {taglist.map((val) => {
                        return (
                        <>
                            <div className="tagRow">
                                <label>{val.tagcount}</label><button onClick={() => TagLink(val.tag)}>{val.tag}</button><button onClick={() => DeleteTag(val.tag_id)}>Delete</button>
                            </div>
                        </>
                        )
                    })}
                </div>
                
            </div>}
        </div>
    )
}
