import React, {useEffect, useState} from 'react';
import NavBar from '../../components/NavBar/NavBar';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';

export default function HomePage() {
    const history = useHistory();

    const [role, GetUserRole] = useState('')
    const [confessionList, GetConfessionList] = useState([])

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                GetUserRole(result.data.user[0].role)
                Axios.get("http://localhost:3001/confession/publish/getall").then((response) => {
                    if (response.data) {
                        GetConfessionList(response.data)
                    }
                })
            }
            else {
                history.push("/")
            }
        });
    }, []); // remove the , [] later on 
    
    return (
        <div className="HomePage">
            <NavBar/>
            <div>
                {confessionList.map((val) => {
                    return (
                        <div className="card">
                            {val.confess_title}
                        </div>
                    )
                })}
            </div>
        </div>
    )
}
