import {React, useState, useEffect} from 'react';
import "./LandingPage.css";
import Modal from "react-responsive-modal";
import { useHistory } from 'react-router-dom';
import Axios from 'axios';
import { Formik, Field, Form, ErrorMessage } from 'formik';

import loginVali from './SignInValidation';
import signUpVali from './SignUpValidation'

export default function LandingPage(){
    const history = useHistory();

    const [SSstatus, setSSstatus] = useState(0); // SS = sign in sign up
    const [signinStatus, GetSignInStatus] = useState(false) // sign in status
    
    // set (Sign in) Status
    const signInS = () => {
        if (signinStatus === true) {
            history.push("/home")
        }
        setSSstatus(1)
    }

    // set Sign Up status
    const signUpS = () => {
        if (signinStatus === true) {
            history.push("/home")
        }
        setSSstatus(2)
    }

    const closemodal = () => {
        if (signinStatus === true) {
            history.push("/home")
        }
        setSSstatus(0)
    }

    const homepage = () => {
        history.push("/home")
    }
    
    const loginfunc = (result) => {
        console.log(result.email)
        Axios.post("http://localhost:3001/signin", {
            email: result.email,
            password: result.password
        }).then((response) => {
            if (response.data.message) {
                console.log(response.data.message)
            } else {
                history.push("/home")
            }
        })
    }

    const signupfunc = (result) => {
        Axios.post("http://localhost:3001/signup", {
            email: result.email,
            dob : result.dob,
            password: result.password,
        }).then((response) => {
            if (response.data.message) {
                console.log(response.data.message);
            }
        })
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                GetSignInStatus(true)
            } 
        });

    }); // remove the , [] later on 

    return(
        <div className="landingPage">
            {signinStatus === false && <div className="LandingDivRight">
                <button id="signIn" onClick={signInS}>Sign In</button>
                <button id="signUp" onClick={signUpS}>Sign Up</button>
            </div>}
            {signinStatus === true && <div className="changeme">
                <button id="alrsignin" onClick={homepage}>Home</button>
            </div>}
            {SSstatus === 1 && <div>
            {/* LOGIN PAGE */}
                <Modal open={true} onClose={closemodal}>
                <div className="LoginPage">
                    <h2>Login Page</h2>
                    <Formik
                    initialValues={{
                    email: "",
                    password: "",
                    }}
                    validationSchema={loginVali}
                    onSubmit={loginfunc}
                    >
                    <Form style={{ display: "flex", flexDirection: "column" }}>

                    <Field name="email" type="email" placeholder="Email..." />
                    <ErrorMessage
                        name="email"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field name="password" type="password" placeholder="Password..." />
                    <ErrorMessage
                        name="password"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <div className="row">
                        <p className="signintext">Don't have an account? Sign Up <a onClick={signUpS}>here</a>.</p>
                        <div className="flex"></div>
                        <button className="btn" type="submit">Submit</button>
                    </div>
                    </Form>
                </Formik>
                </div>
                </Modal>
            </div>}
            {/* SIGN UP PAGE */}
            {SSstatus === 2 && <div>
                <Modal open={true} onClose={closemodal}>
                    <div className="SignUpPage">
                    <h2>Sign up Page</h2>
                    <Formik
                        initialValues={{
                        email: "",
                        dob: "",
                        password: "",
                        }}
                        validationSchema={signUpVali}
                        onSubmit={signupfunc}
                    >
                    <Form style={{ display: "flex", flexDirection: "column" }}>
                    <Field className="form-control" name="email" type="email" placeholder="Email..." />
                    <ErrorMessage
                        name="email"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="form-control" name="dob" type="date" placeholder="Date of Birth" />
                    <ErrorMessage
                        name="dob"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <Field className="password" name="password" type="password" placeholder="Password" />
                    <ErrorMessage
                        name="password"
                        component="div"
                        className="invalid-feedback"
                        style={{ color: "red", fontWeight: "bold" }}
                    />

                    <div className="row">
                        <p className="signintext">Have an account? Login <a onClick={signInS}>here</a>.</p>
                        <div className="flex"></div>
                        <button className="btn" type="submit">Submit</button>
                    </div>
                    </Form>
                </Formik>
                </div>
                </Modal>
            </div>}
        </div>
    )
}
