import {React, useState, useEffect} from 'react';
import Axios from 'axios';
import { useHistory } from 'react-router-dom';
import {
    BrowserRouter as Router,
    Switch,
    Route,
  } from "react-router-dom";

import { CKEditor } from '@ckeditor/ckeditor5-react';
import Editor from 'ckeditor5-custom-build/build/ckeditor';

import Select from 'react-select';

import useUnsavedChangesWarning from "../../hooks/useUnsavedChangesWarning";
import "./MCPage.css"

import LoginPage from "../LoginPage/LoginPage";
import SignUpPage from "../SignUpPage/SignUpPage";
import HomePage from "../HomePage/HomePage";
import EditablePage from "../EditablePage/EditablePage";

// import NavBar 
import NavBar from "../../components/NavBar/NavBar";

export default function MCPage(){
    const history = useHistory();

    const [Prompt, setDirty, setPristine] = useUnsavedChangesWarning();
    
    const [role, GetUserRole] = useState('')
    const [create, GetCreate] = useState(false)
    const [publishstatus, GetPublishStatus] = useState('')

    const [title, GetTitle] = useState('')
    const [content, GetContent] = useState('')
    const [location, GetLocation] = useState('')
    const [timer, GetTimer] = useState('0')
    const [Tag, GetTag] = useState([])

    const editorConfiguration = {
        placeholder: "Type your confession here...",
        toolbar: ["bold", "italic", "underline"]
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                GetUserRole(result.data.user[0].role)
            } 
            else {
                history.push("/")
            }
        });

    }, []); // remove the , [] later on 

    const createconfession = () => {
        Axios.post("http://localhost:3001/createconfession", {
        })
        GetCreate(true)
    }

    const savetoDraft = () => {
        console.log("savetodraft frontend")
        setPristine();
        Axios.post("http://localhost:3001/updateconfession/draft", {
            location: location,
            content: content,
            title: title,
        }).then((response) => {
            if (response.data.message) {
                GetPublishStatus(response.data.message)
            }
        })
        history.push("/")
    }

    const publishConfession = () => {
        let usertimer = 0
        var tagarray = []
        if (timer.value !== undefined) {
            usertimer = timer.value
        }

        for (var i = 0; i < Tag.length; i++) {
            delete Tag[i].label
            tagarray.push(Tag[i].value)
        } 

        Axios.post("http://localhost:3001/updateconfession/publish", {
            location: location,
            content: content,
            title: title,
            timer: usertimer,
            tag: tagarray,
        }).then((response) => {
            if (response.data.message) {
                GetPublishStatus(response.data.message)
            }
        })
        history.push("/")
    }

    const TimerOption = [
        {value: '12', label: 'Half Day'},
        {value: '24', label: 'One Day'},
        {value: '48', label: 'Two Days'},
        {value: '72', label: 'Three Days'},
        {value: '0', label: 'Permanent'},
    ]

    const TagOption = [
        {value: 'relationship', label: 'Relationship'},
        {value: 'thief', label: 'Thief'},
        {value: 'sabotage', label: 'Sabotage'},
        {value: 'karma', label: 'Karma'},
        {value: 'bday', label: 'Birthday'},
        {value: 'eventruin', label: 'Event Ruiner'},
    ]

    return (
        
        <div className="MCPage">
            <NavBar/>
            {/* 
          <Route path="/login"> 
            <LoginPage/>
          </Route>
          <Route path="/signup">
            <SignUpPage/>
          </Route>
          <Route path="/draft">
            <EditablePage/>
          </Route>
          <Route path="/makeconfession">
            <MCPage/>
          </Route>
          <Route path="/">
            <HomePage/>
          </Route> */}
            <h2>Make Confession Page</h2>
            {create === false && <button onClick={createconfession}>Create confession</button>}
            {create === true && <div className="Box">
                <div>
                    <label> Title:
                        <input 
                        maxLength = "250"
                        placeholder="Spice your confession!"
                        onChange={(e) => {
                            GetTitle(e.target.value);
                            setDirty();
                        }}
                        />
                    </label>
                </div>
                <div>
                    <CKEditor
                        editor = { Editor }
                        data = {content}
                        config = {editorConfiguration}
                        onChange = {(event, editor) => {
                            const data = editor.getData()
                            GetContent(data);
                            setDirty();
                        }}                       
                    />
                </div>
                <div>
                    <label> Crime scene:
                        <input 
                        maxLength = "150"
                        placeholder="Where did it take place?"
                        onChange={(event) => {
                            GetLocation(event.target.value);
                            setDirty();
                        }}
                        />
                    </label>
                </div>
                <Select
                    defaultValue = {timer}
                    onChange = {GetTimer}
                    options = {TimerOption}/>
                <Select
                    defaultValue = {Tag}
                    onChange = {GetTag}
                    isMulti
                    options = {TagOption}/>
                <button onClick={() => {
                    savetoDraft();
                    setPristine();
                }}>Save to Draft</button>
                <button onClick={publishConfession}>Publish Confession</button>
                <p>{publishstatus}</p>
            </div>}
            {Prompt}
        </div>
    )
}
