import {React, useState, useEffect} from 'react';
import { useHistory } from 'react-router-dom';
import Axios from 'axios';

import { CKEditor } from '@ckeditor/ckeditor5-react';
import Editor from 'ckeditor5-custom-build/build/ckeditor';

import Modal from "react-responsive-modal";
import Select from 'react-select';

// import NavBar 
import NavBar from "../../components/NavBar/NavBar";

import "./EditablePage.css"

export default function EditablePage(){
    const history = useHistory();

    const [openDraft, GotDraft] = useState(false);
    const [publishstatus, GetPublishStatus] = useState('');
    const [draftList, GetDraftList] = useState([]);

    const [location, SetLocation] = useState('');
    const [title, SetTitle] = useState('');
    const [content, SetContent] = useState('');
    const [timer, SetTimer] = useState('0');
    const [Tag, SetTag] = useState([]);
    const [modalStatus, GetModalStatus] = useState(false);

    const editorConfiguration = {
        placeholder: "Type your confession here...",
        toolbar: ["bold", "italic", "underline"]
    }

    useEffect(() => {
        Axios.get("http://localhost:3001/getaccount").then((result) => {
            if (result.data.loggedIn === true) {
                Axios.get("http://localhost:3001/confession/getdraft").then((response) => {
                    if (response.data) {
                        GetDraftList(response.data)
                    }
                })
            } else {
                history.push("/")
            }
        });
        if (content !== "") {
            window.addEventListener('beforeunload', alertUser)
            return () => {
                window.removeEventListener('beforeunload', alertUser)
            }
        }
    }); // remove the , [] later on 

    const alertUser = (e) => {
        e.preventDefault();
        e.returnValue = ''
    }

    const closemodal = () => {
        GetModalStatus(false)
    }

    const savetoDraft = () => {
        if (title === "") {
            GetPublishStatus("Add a title before saving to draft!")
            GetModalStatus(true)
        } else {
            Axios.post("http://localhost:3001/updateconfession/draft", {
            location: location,
            content: content,
            title: title,
            }).then((response) => {
                if (response.data.message) {
                    GetPublishStatus(response.data.message)
                    GetModalStatus(true)
                }
            })
            history.push("/home")
        }
    }

    const publishConfession = () => {
        let usertimer = 0
        var tagarray = []

        if (timer.value !== undefined) {
            usertimer = timer.value
        }

        for (var i = 0; i < Tag.length; i++) {
            delete Tag[i].label
            tagarray.push(Tag[i].value)
        } 

        if (location === "" || content === "" || title === "" || usertimer === 0 || tagarray.length === 0) {
            GetPublishStatus("We cannot publish your confession while some of the field is empty!")
            GetModalStatus(true)
        } else {
            console.log("publish")
            Axios.post("http://localhost:3001/updateconfession/publish", {
            location: location,
            content: content,
            title: title,
            timer: usertimer,
            tag: tagarray,
            }).then((response) => {
                if (response.data.message) {
                    GetPublishStatus(response.data.message)
                    GetModalStatus(true)
                }
            })
            history.push("/home")
        }
    }

    const FindDraftID = (conID) => {
        const currentID = `${conID}`
        GotDraft(true)
        Axios.post("http://localhost:3001/confession/store/id", {
            confessID: currentID
        })

        Axios.post("http://localhost:3001/confession/getdraft/specific", {
            confessID: currentID
        }).then((response) => {
            if (response.data) {
                var array = Object.values(response.data[0])
                console.log(array[4])
                console.log(array)
                SetLocation(array[2])
                SetTitle(array[3])
                if (array[4] !== null) {
                    SetContent(array[4])
                }
            }
        })
    }

    const TimerOption = [
        {value: '12', label: 'Half Day'},
        {value: '24', label: 'One Day'},
        {value: '48', label: 'Two Days'},
        {value: '72', label: 'Three Days'},
        {value: '0', label: 'Permanent'},
    ]

    const TagOption = [
        {value: 'relationship', label: 'Relationship'},
        {value: 'thief', label: 'Thief'},
        {value: 'sabotage', label: 'Sabotage'},
        {value: 'karma', label: 'Karma'},
        {value: 'bday', label: 'Birthday'},
        {value: 'eventruin', label: 'Event Ruiner'},
    ]

    return (
        <div className="DraftPage">
            <NavBar/>
            <div className="draftContainer">
                 <h2>Your draft</h2>
                    {openDraft === false && <div>
                    {draftList.map((val) => {
                        return (
                            <div className="list-draft-box">
                                <div className="clickable-box" onClick={() => {FindDraftID(val.confess_id)}}>
                                    {val.confess_title}
                                </div>
                            </div>
                        )
                    })}
                </div>}
                {openDraft === true && <div>
                    <div>
                        <label> Title:
                            <input 
                            value = {title}
                            maxLength = "250"
                            placeholder="Spice your confession!"
                            onChange={(e) => {
                                SetTitle(e.target.value);
                            }}
                            />
                        </label>
                    </div>
                    <div>
                        <CKEditor
                            editor = { Editor }
                            data = {content}
                            config = {editorConfiguration}
                            onChange = {(event, editor) => {
                                const data = editor.getData()
                                SetContent(data);
                            }}                       
                        />
                    </div>
                    <div>
                        <label> Crime scene:
                            <input 
                            value = {location}
                            maxLength = "150"
                            placeholder="Where did it take place?"
                            onChange={(event) => {
                                SetLocation(event.target.value);
                            }}
                            />
                        </label>
                    </div>
                    <Select
                        defaultValue = {timer}
                        onChange = {SetTimer}
                        options = {TimerOption}/>
                    <Select
                        defaultValue = {Tag}
                        onChange = {SetTag}
                        isMulti
                        options = {TagOption}/>
                    <button onClick={() => {
                        savetoDraft();
                    }}>Save to Draft</button>
                    <button onClick={publishConfession}>Publish Confession</button>
                    {modalStatus === true && <Modal open={true} onClose={closemodal}>
                        <div>
                            <p>{publishstatus}</p>
                            <button onClick={closemodal}>Close</button>
                        </div>
                    </Modal>}
                </div>}
            </div>
           
        </div>
    )
}
