import {React, useState} from 'react';
import "./LandingPage.css";
import Modal from "react-responsive-modal";
import {
    BrowserRouter as Router,
    Switch,
    Route,
  } from "react-router-dom";

import { useHistory } from 'react-router-dom';
import { Component } from 'react';
import Axios from 'axios';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import HomePage from "../HomePage/HomePage";

import Validation from './Validation';

class LandingPage extends Component {
    
    constructor(props) {
        super(props)

        this.state = {
            sign : false,
            login : false,
        }
    }

    onOpenModal = () => {
        this.setState({sign: true});
    };

    onOpenModalLogin = () => {
        this.setState({login: true});
    };

    onCloseModal = () => {
        this.setState({sign: false});
    };

    onCloseModalLogin = () => {
        this.setState({login: false});
    };

    handleClick = e => {
        this.props.history.push("/home");
    };
    

    render() {
        /////////////////////// Sign up
        const {login, sign} = this.state;

        const signup = (result) => {
            Axios.post("http://localhost:3001/", {
                firstname: result.firstName,
                lastname: result.lastName,
                username: result.userName,
                email: result.email,
                dob : result.dob,
                password: result.password,
            }).then((response) => {
                if (response.data.message) {
                    console.log(response.data.message);
                }
            })
        }

        ////////////////////// Login

        const loginfunc = (result) => {
        Axios.post("http://localhost:3001/", {
            usernameLogin: result.userName,
            passwordLogin: result.password
        }).then((response) => {
            if (response.data.message) {
                console.log(response.data.message)
            } else {
                this.props.history.push("/home")
            }
        })
    }

        return(
            <>
        <div className="LandingDiv">

            <Router>
                <Route path="/home" component={HomePage}/>
            </Router>

            <div className="LandingDivLeft">

            </div>
            <div className="LandingDivRight">
                <button id="signUp" onClick={this.onOpenModal}>Sign Up</button>
                <button id="login" onClick={this.onOpenModalLogin}>Sign In</button>
            </div>
{/* SIGN UP PAGE */}
            <Modal open={sign} onClose={this.onCloseModal}>
                <div className="SignUpPage">
                <h2>Sign up Page</h2>
                <Formik
                    initialValues={{
                    firstName: "",
                    lastName: "",
                    userName: "",
                    email: "",
                    dob: "",
                    password: "",
                    }}
                    validationSchema={Validation}
                    onSubmit={signup}
                >
                <Form style={{ display: "flex", flexDirection: "column" }}>
                <Field className="form-control" name="firstName" type="text" placeholder="First Name" />
                <ErrorMessage
                    name="firstName"
                    component="div"
                    className="invalid-feedback"
                    style={{ color: "red", fontWeight: "bold" }}
                />

                <Field className="form-control" name="lastName" type="text" placeholder="Last Name" />
                <ErrorMessage
                    name="lastName"
                    component="div"
                    className="invalid-feedback"
                    style={{ color: "red", fontWeight: "bold" }}
                />

                <Field className="form-control" name="userName" type="text" placeholder="User Name" />
                <ErrorMessage
                    name="userName"
                    component="div"
                    className="invalid-feedback"
                    style={{ color: "red", fontWeight: "bold" }}
                />

                <Field className="form-control" name="email" type="email" placeholder="Email" />
                <ErrorMessage
                    name="email"
                    component="div"
                    className="invalid-feedback"
                    style={{ color: "red", fontWeight: "bold" }}
                />

                <Field className="form-control" name="dob" type="date" placeholder="Date of Birth" />
                <ErrorMessage
                    name="dob"
                    component="div"
                    className="invalid-feedback"
                    style={{ color: "red", fontWeight: "bold" }}
                />

                <Field className="password" name="password" type="password" placeholder="Password" />
                <ErrorMessage
                    name="password"
                    component="div"
                    className="invalid-feedback"
                    style={{ color: "red", fontWeight: "bold" }}
                />

                <div className="row">
                    <p className="signintext">Have an account? Login <a onClick={this.onOpenModalLogin}>here</a>.</p>
                    <div className="flex"></div>
                    <button className="btn" type="submit">Submit</button>
                </div>
                </Form>
            </Formik>
            </div>
            </Modal>

{/* LOGIN PAGE */}
            <Modal open={login} onClose={this.onCloseModalLogin}>
            <div className="LoginPage">
            <div>
                <h2>Login Page</h2>
                <Formik
                initialValues={{
                userName: "",
                password: "",
                }}
                validationSchema={Validation}
                onSubmit={this.handleClick}
            >
            <Form style={{ display: "flex", flexDirection: "column" }}>

            <Field name="userName" type="text" placeholder="Username or Email" />
            <ErrorMessage
                name="userName"
                component="div"
                className="invalid-feedback"
                style={{ color: "red", fontWeight: "bold" }}
            />

            <Field name="password" type="password" placeholder="Password" />
            <ErrorMessage
                name="password"
                component="div"
                className="invalid-feedback"
                style={{ color: "red", fontWeight: "bold" }}
            />

            <div className="row">
                <p className="signintext">Don't have an account? Sign Up <a href="/signup">here</a>.</p>
                <div className="flex"></div>
                <button className="btn" type="submit">Submit</button>
            </div>
            </Form>
        </Formik>
            </div>
        </div>
            </Modal>
        </div>

        </>
        )
    }
     
}

export default LandingPage
